    //Solution 2:Using bitwise
    public int sumIndicesWithKSetBits2(List<Integer> nums, int k) {
        int[] res = new int[nums.size() +1];
        int sum = 0;
        for(int i = 0; i<nums.size(); i++){
            res[i] = res[i>>1] + (i&1);
            if(res[i] == k){
                sum += nums.get(i);
            }
        }
        return sum;
    }
